import { useQueryMachine} from '../../stateMachines/queryMachine'

import LargeQuery from '../largeQuery';
import UserInputField from '../userInputField';

export default function ChatModule(){
    const { conversationStarted, setConversationStarted } = useQueryMachine();
    if (!conversationStarted){
        return(
            <div className="w-full">
                <LargeQuery queryString={"What are we building?"}/>
                <UserInputField onSubmit={(response)=>handleSubmit(response)}/>
            </div>
        )
    }

    else{
        return (
            <div>we started the convo</div>
        )}

}
