import React, { useState } from 'react';

interface ChatBubbleProps {
  userName: string;
  message: string;
  timestamp: number;
  isUser: boolean;
}

const ChatBubble: React.FC<ChatBubbleProps> = ({
  userName,
  message,
  timestamp,
  isUser,
}) => {
  const [showInfo, setShowInfo] = useState(false);

  const formattedTimestamp = new Date(timestamp).toLocaleString();

  return (
    <div
      className={`flex ${isUser ? 'justify-end' : 'justify-start'} mb-4`}
    >
      <div
        className={`relative max-w-lg p-4 rounded-lg ${
          isUser ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'
        }`}
        onMouseEnter={() => setShowInfo(true)}
        onMouseLeave={() => setShowInfo(false)}
      >
        <div>{message}</div>
        <div
          className={`absolute bottom-0 transform ${
            isUser ? 'right-0 translate-x-2' : 'left-0 -translate-x-2'
          }`}
          style={{
            borderWidth: '6px',
            borderStyle: 'solid',
            borderColor: `${isUser ? 'transparent transparent transparent blue' : 'transparent gray transparent transparent'}`,
          }}
        ></div>
        {showInfo && (
          <div className={`mt-2 text-xs italic ${isUser ? 'text-right' : ''}`}>
            {userName} at {formattedTimestamp}::{isUser}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatBubble;
