import React, { createContext, useContext, useState, ReactNode } from 'react';

// Define states
const STATE_ONE = 'STATE_ONE';
const STATE_TWO = 'STATE_TWO';

// Define the shape of the StateMachineContext
interface StateMachineContextType {
  state: string;
  transitionToStateTwo: () => void;
  transitionToStateOne: () => void;
}

// Create Context with an initial value of undefined
const StateMachineContext = createContext<StateMachineContextType | undefined>(undefined);

// Define the provider component
export const QueryMachine = ({ children }: { children: ReactNode }) => {
  const [state, setState] = useState(STATE_ONE);

  const transitionToStateTwo = () => {
    setState(STATE_TWO);
  };

  const transitionToStateOne = () => {
    setState(STATE_ONE);
  };

  const value = {
    state,
    transitionToStateTwo,
    transitionToStateOne,
  };

  return (
    <StateMachineContext.Provider value={value}>
      {children}
    </StateMachineContext.Provider>
  );
};

// Custom hook to use the StateMachineContext
export const useQueryMachine = () => {
  const context = useContext(StateMachineContext);
  if (!context) {
    throw new Error('useStateMachine must be used within a QueryMachine');
  }
  return context;
};
